package co.lucjay.shop.member.VO;

public class MemberVO {
	private String name;
	private String id;
	private String pw;
	private String addr;

	public MemberVO() {
		// TODO Auto-generated constructor stub
	}

	public String getName() {
		return name;
	}

	public String getId() {
		return id;
	}

	public String getPw() {
		return pw;
	}

	public String getAddr() {
		return addr;
	}

	public void setName(String name) {
		this.name = name;
	}

	public void setId(String id) {
		this.id = id;
	}

	public void setPw(String pw) {
		this.pw = pw;
	}

	public void setAddr(String addr) {
		this.addr = addr;
	}

}
